import React, { useState, useEffect } from 'react';
import { createProduct, updateProduct } from '../services/api';

const ProductForm = ({ selectedProduct, clearSelection }) => {
  const [product, setProduct] = useState({
    name: '',
    value: '',
    description: '',
  });

  useEffect(() => {
    if (selectedProduct) {
      setProduct(selectedProduct);
    } else {
      setProduct({
        name: '',
        value: '',
        description: '',
      });
    }
  }, [selectedProduct]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct({ ...product, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (selectedProduct) {
      await updateProduct(selectedProduct.idProduct, product);
    } else {
      await createProduct(product);
    }
    clearSelection();
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>{selectedProduct ? 'Edit' : 'Create'} Product</h2>
      <input
        type="text"
        name="name"
        placeholder="Name"
        value={product.name}
        onChange={handleChange}
        required
      />
      <input
        type="number"
        name="value"
        placeholder="Value"
        value={product.value}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="description"
        placeholder="Description"
        value={product.description}
        onChange={handleChange}
        required
      />
      <button type="submit">{selectedProduct ? 'Update' : 'Create'}</button>
      <button type="button" onClick={clearSelection}>Cancel</button>
    </form>
  );
};

export default ProductForm;
